const MONTHS = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
    'August',
    'September',
    'October',
    'November',
    'December'
];

const WEEKDAYS = [
    'Sunday',
    'Monday',
    'Tuesday',
    'Wednesday',
    'Thursday',
    'Friday',
    'Saturday'
];

// Find the first weekday in text (if any) and return index or -1.
function guessWeekday(text) {
    for (let i = 0; i < WEEKDAYS.length; i++) {
        if (text.includes(WEEKDAYS[i])) {
            return i;
        }
    }
    return -1;
}

// Return random item from array.
function choice(arr) {
    return arr[(Math.random() * arr.length) | 0];
}

// Load array of command sources, calling each .init(voice) method
function loadSources(voice, sources) {
    if (sources.length === 0) {
        return Promise.resolve(null);
    }
    const s = sources[0];
    sources = sources.slice(1);
    return import('../../plugins/' + s.file).then(m => {
        if (typeof m.init === 'function') {
            m.init(voice, s.options);
        }
        return loadSources(voice, sources);
    }).catch(e => {
        createUpdate('Failed to load: ' + s.file, {type: 'info'});
        return loadSources(voice, sources);
    });
}

// Scroll to bottom of page.
function scroll() {
    setTimeout(() => window.scrollTo(0, document.body.scrollHeight), 0);
}

// Play a beep sound.
function beep() {
    const sound = new Audio("data:audio/wav;base64,");
    sound.play();
}

/*
Create a visual update box in the main window.
Options can include:
    "type": "computer", "human", or "info".
*/
function createUpdate(msg, options) {
    options = options || {};
    const div = document.createElement('div');
    if (typeof options.type !== 'undefined') {
        div.className = options.type;
    }
    const el = document.createElement('div');
    div.appendChild(el);
    el.innerText = msg;
    document.querySelector('main').appendChild(div);
    scroll();
    return el;
}
